import React, { useContext, useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimesCircle } from "@fortawesome/free-solid-svg-icons";
import "./reserve.css";
import useFetch from "../../hooks/useFetch";
import { SearchContext } from "../../context/SearchContext";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Reserve({ setOpen, hotelId }) {
  const [selectedRooms, setSelectedRooms] = useState([]);
  const { data, loading, error } = useFetch(`http://localhost:8800/api/hotels/room/${hotelId}`);
  const { dates } = useContext(SearchContext);

  const getDatesInRange = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);

    const date = new Date(start.getTime());

    const dates = [];

    while (date <= end) {
      dates.push(new Date(date).getTime());
      date.setDate(date.getDate() + 1);
    }

    return dates;
  };


  const alldates = getDatesInRange(dates[0].startDate, dates[0].endDate);

  const isAvailable = (roomNumber) => {
    const isFound = roomNumber.unavailableDates.some((date) =>
      alldates.includes(new Date(date).getTime())
    );

    return !isFound;
  };
  const handleSelect = (e) => {
    const checked = e.target.checked;
    const value = e.target.value;
    setSelectedRooms(
      checked
        ? [...selectedRooms, value]
        : selectedRooms.filter((item) => item !== value)
    );
  };
  const handleClick = async () => {
    try {
      await Promise.all(
        selectedRooms.map((roomId) => {
          const res = axios.put(`http://localhost:8800/api/rooms/availability/${roomId}`, {
            dates: alldates,
          });
          return res.data;
        })
      );
      setOpen(false);
      navigate("/");
    } catch (err) {}
  };

  const navigate = useNavigate();


  return (
    <div className="reserve">
    <div className="rContainer">
      <FontAwesomeIcon
        icon={faCircleTimes}
        className="rClose"
        onClick={() => setOpen(false)}
      />
      <span>Select your rooms:</span>
      {data?.map((item) => (
        // Check if item exists
        <div className="rItem" key={item?._id}>
          <div className="rItemInfo">
            <div className="rTitle">{/*item./*title*/}</div>
            <div className="rDesc">{item?.desc}</div> {/* Check if item.desc exists */}
            <div className="rMax">
              Max people: <b>{item?.maxPeople}</b>
            </div>
            <div className="rPrice">{item?.price}</div>
          </div>
          <div className="rSelectRooms">
            {item?.roomNumbers?.map((roomNumber) => (
              <div className="room" key={roomNumber?._id}>
                <label>{roomNumber?.number}</label>
                <input
                  type="checkbox"
                  value={roomNumber?._id}
                  onChange={handleSelect}
                  disabled={!isAvailable(roomNumber)}
                />
              </div>
            ))}
          </div>
        </div>
      ))}
      <button onClick={handleClick} className="rButton">
        Reserve Now!
      </button>
    </div>
  </div>
  )
}
